import { CHANGE_TOP_BANNERS } from "./constant";
import { getTopBanners } from "network/recommend";

export const getTopBannerAction = () => {
  return (dispatch) => {
    getTopBanners().then((res) => {
      dispatch({ type: CHANGE_TOP_BANNERS, topBanners: res.banners });
    });
  };
};
